###############################################################################
#
# Environment set up
#
###############################################################################

shopt -s nullglob # No-match globbing expands to null
Tmp=/tmp/`basename $0`-${$}-
trap CleanUp INT

###############################################################################
#
# Functions
#
###############################################################################

Usage() {
cat <<EOF
Usage: `basename $0` arg1 [arg2]

How this works

_________________________________________________________________________
\$Id: Proto.sh,v 1.1 2015/01/08 11:51:51 nichols Exp $
EOF
exit
}

CleanUp () {
    /bin/rm -f /tmp/`basename $0`-${$}-*
    exit 0
}


###############################################################################
#
# Parse arguments
#
###############################################################################

while (( $# > 1 )) ; do
    case "$1" in
        "-help")
            Usage
            ;;
#         "-t")
#             shift
#             tval="$1"
#             shift
#             ;;
        -*)
            echo "ERROR: Unknown option '$1'"
            exit 1
            break
            ;;
        *)
            break
            ;;
    esac
done
Tmp=$TmpDir/f2r-${$}-

if (( $# < 1 )) ; then
    Usage
fi

###############################################################################
#
# Script Body
#
###############################################################################

for d in "$@" ; do

  echo "Arugment: '$d'"
    
done


###############################################################################
#
# Exit & Clean up
#
###############################################################################

CleanUp

